import collections
import itertools
from test_harness.harness import harness_run

class Solution:
    def threeSumMulti(self, arr, target):

        d = collections.Counter(arr)
        arrkeys = list(d.keys())
        arrkeys.sort()

        ans = 0
        def twosum(start, end, target):
            rtn = 0
            while start <= end:
                sm = arrkeys[start] + arrkeys[end]
                if sm == target:
                    if start == end:
                        rtn += (d[arrkeys[start]] * (d[arrkeys[start]] - 1)) // 2
                    else:
                        rtn += d[arrkeys[start]] * d[arrkeys[end]]
                    start += 1
                elif sm < target:
                    start += 1
                else:
                    end -= 1
            return rtn


        for i in range(len(arrkeys)):
            freq = d[arrkeys[i]]
            # first only one arrkeys[i]
            need = target - arrkeys[i]
            rtn = twosum(i + 1, len(arrkeys) - 1, need)
            if rtn:
                ans += freq * rtn

            # second, only with two arrkeys[i]
            if freq > 1:
                need = target - arrkeys[i] * 2
                if need > arrkeys[i]:
                    ans += ((freq * (freq - 1)) // 2) * d.get(need, 0)
            
            # third, with all 3 being arrkeys[i]
            if freq > 2 and 3 * arrkeys[i] == target:
                # choose theorem
                ans += ((freq * (freq - 1) * (freq - 2)) // (3 * 2))
            
            # delete so that we don't double count when doing 2nd phase
            d.pop(arrkeys[i])

        return ans % (10 ** 9 + 7)


# alternate solution
# do nested double loop with Counter keys and use dict lookup
# no twosum
    def threeSumMulti1(self, A, target):
        c = collections.Counter(A)
        res = 0
        for i, j in itertools.combinations_with_replacement(c, 2):
            k = target - i - j
            if i == j == k: res += c[i] * (c[i] - 1) * (c[i] - 2) / 6
            elif i == j != k: res += c[i] * (c[i] - 1) / 2 * c[k]
            elif k > i and k > j: res += c[i] * c[j] * c[k]
        return res % (10**9 + 7)
    
    
